variable.value:
{
"title": "OSDM Online Schemas for schema validation",
"description": "OSDM Online Schemas for schema validation",
"definitions": {	

  "BadRequestResponse": {
    "description": "Bad Request (400)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "UnauthorizedResponse": {
    "description": "Unauthorized (401)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "ForbiddenResponse": {
    "description": "Forbidden (403)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "NotFoundResponse": {
    "description": "Not Found (404)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "NotAcceptableResponse": {
    "description": "Not Acceptable (406)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "ConflictResponse": {
    "description": "Conflict (409)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "UnsupportedMediaTypeResponse": {
    "description": "Unsupported Media Type (415)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "InternalServerErrorResponse": {
    "description": "Internal Server Error (500)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "NotImplementedResponse": {
    "description": "Not Implemented (501)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "ServiceUnavailableResponse": {
    "description": "Service Unavailable (503)\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "DefaultErrorResponse": {
    "description": "default error response\n",
    "content": {
      "application/problem+json": {
        "schema": {
          "$ref": "#/definitions/Problem"
        }
      }
    }
  },
  "FulfillmentRequestAcceptedResponse": {
    "description": "Fulfillment successfully initiated, retrieve booking to retrieve the fulfillment documents\n",
    "content": {
      "application/json": {
        "schema": {
          "$ref": "#/definitions/WarningCollection"
        }
      }
    }
  },
  "WarningCollection": {
    "type": "object",
    "additionalProperties": false,
    "description": "List of warnings.\n",
    "properties": {
      "warnings": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/Warning"
        }
      }
    }
  },
  "Warning": {
    "type": "object",
    "additionalProperties": false,
    "description": "This element can be used to pass non-blocking information or events,\nsuch as a price difference with the initially offered price at booking time.\nIt is inspired on the JSon problem structure.\n",
    "required": [
      "code"
    ],
    "properties": {
      "code": {
        "type": "string",
        "nullable": false
      },
      "type": {
        "description": "An absolute URI that identifies the warning type. When dereferenced,\nit SHOULD provide human-readable documentation for the problem type\n(e.g., using HTML).\n",
        "type": "string",
        "format": "uri",
        "default": "about:blank",
        "example": "https://example.com/warns/price-updated"
      },
      "detail": {
        "description": "A human readable explanation specific to this occurrence of the\nwarning.\n",
        "type": "string",
        "example": "The price of the given offer part has been updated during the booking operation"
      },
      "instance": {
        "description": "An absolute URI that identifies the specific occurrence of the warning.\n",
        "type": "string",
        "format": "uri",
        "example": "offers/offer1234"
      }
    }
  },
  "PlaceResponse": {
    "type": "object",
    "additionalProperties": false,
    "required": [
      "places"
    ],
    "properties": {
      "warnings": {
        "$ref": "#/definitions/WarningCollection"
      },
      "places": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/Place"
        },
        "minItems": 1
      }
    }
  },
  "TripResponse": {
    "type": "object",
    "additionalProperties": false,
    "required": [
      "trip"
    ],
    "properties": {
      "warnings": {
        "$ref": "#/definitions/WarningCollection"
      },
      "trip": {
        "$ref": "#/definitions/Trip"
      }
    }
  },
  "PlaceRequest": {
    "type": "object",
    "additionalProperties": false,
    "description": "Initial input for the place information request. This input defines what is originally looked for.\nProvided by OJP.\n",
    "properties": {
      "placeInput": {
        "$ref": "#/definitions/InitialPlaceInput"
      },
      "restrictions": {
        "$ref": "#/definitions/PlaceParam"
      }
    }
  },
  "PlaceParam": {
    "type": "object",
    "additionalProperties": false,
    "description": "More parameters for restricting the request. Provided by OJP.\n",
    "properties": {
      "type": {
        "$ref": "#/definitions/PlaceType"
      },
      "usage": {
        "$ref": "#/definitions/PlaceUsage"
      },
      "ptModes": {
        "$ref": "#/definitions/ModeFilter"
      },
      "serviceBrandFilter": {
        "$ref": "#/definitions/ServiceBrandFilter"
      },
      "carrierFilter": {
        "$ref": "#/definitions/CarrierFilter"
      },
      "pointOfInterestFilter": {
        "$ref": "#/definitions/PointOfInterestFilter"
      },
      "numberOfResults": {
        "description": "Maximum number of results to be returned. The service is allowed to return fewer objects if reasonable\nor otherwise appropriate.\n",
        "type": "integer",
        "format": "int32",
        "minimum": 1
      }
    }
  },
  "PlaceType": {
    "description": "Provided by OJP.\n",
    "type": "string",
    "enum": [
      "STOP",
      "ADDRESS",
      "POI",
      "GEO_COORDINATE",
      "TOPOGRAPHIC_PLACE"
    ]
  },
  "PlaceUsage": {
    "description": "Provided by OJP.\n",
    "type": "string",
    "enum": [
      "ORIGIN",
      "VIA",
      "DESTINATION"
    ]
  },
  "InitialPlaceInput": {
    "type": "object",
    "additionalProperties": false,
    "description": "Initial input for the place information request. This input defines what is originally looked for.\nProvided by OJP.\n",
    "properties": {
      "name": {
        "description": "Name of the place object which is looked after. This is usually the user's input. If not given,\nthe name of the resulting place objects is not relevant.\n",
        "type": "string",
        "example": "Bern Brengraben"
      },
      "geoPosition": {
        "$ref": "#/definitions/GeoPosition"
      }
    }
  },
  "TripSearchCriteria": {
    "type": "object",
    "additionalProperties": false,
    "description": "Trip request structure. Provided by OJP.\n",
    "required": [
      "origin",
      "destination"
    ],
    "properties": {
      "departureTime": {
        "type": "string",
        "pattern": "(\\d{4}-[01]\\d-[0-3]\\dT[0-2]\\d:[0-5]\\d:[0-5]\\d)"
      },
      "arrivalTime": {
        "description": "requested arrival time. If arrival time is provided the departure time must be omitted.\n",
        "type": "string",
        "pattern": "(\\d{4}-[01]\\d-[0-3]\\dT[0-2]\\d:[0-5]\\d:[0-5]\\d)"
      },
      "origin": {
        "$ref": "#/definitions/PlaceRef"
      },
      "destination": {
        "$ref": "#/definitions/PlaceRef"
      },
      "vias": {
        "description": "Ordered series of points where the journey must pass through. If more\nthan one via point is given all of them must be obeyed - in the correct\norder. The server is allowed to replace a via stop by equivalent stops.\n",
        "type": "array",
        "items": {
          "$ref": "#/definitions/TripVia"
        }
      },
      "parameters": {
        "$ref": "#/definitions/TripParameters"
      },
      "embed": {
        "description": "Influences whether referenced resources are returned in full or as references only. Proposed default ALL\n",
        "type": "array",
        "items": {
          "$ref": "#/definitions/TripsCollectionResponseContent"
        }
      },
      "returnSearchParameters": {
        "$ref": "#/definitions/ReturnSearchParameters"
      },
      "notVias": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/NotVia"
        }
      }
    }
  },
  "TripVia": {
    "type": "object",
    "additionalProperties": false,
    "description": "Via restrictions for a trip. Provided by OJP.\n",
    "required": [
      "viaPlace"
    ],
    "properties": {
      "viaPlace": {
        "$ref": "#/definitions/PlaceRef"
      },
      "dwellTime": {
        "description": "desired dwell time at the via place\n",
        "type": "string",
        "format": "duration",
        "example": "30M"
      }
    }
  },
  "BaseTripPolicyFilter": {
    "type": "object",
    "additionalProperties": false,
    "description": "Policies that control the trip search behavior for both public and individual transport.\nProvided by OJP.\n",
    "properties": {
      "noSingleStep": {
        "description": "The user is not able to climb one step, i.e. wheel chair.\n",
        "type": "boolean",
        "default": false
      },
      "noSight": {
        "description": "The user is not able to see.\n",
        "type": "boolean",
        "default": false
      }
    }
  },
  "TripMobilityFilter": {
    "type": "object",
    "additionalProperties": false,
    "description": "Base mobility options to be applied for both public and individual transport.\nProvided by OJP.\n",
    "properties": {
      "bikeTransport": {
        "description": "The user wants to carry a bike on public transport.\n",
        "type": "boolean",
        "default": false
      },
      "walkSpeed": {
        "description": "Deviation from average walk speed in percent. 100% percent is average speed.\n",
        "type": "integer",
        "format": "int32",
        "default": 100
      },
      "additionalTransferTime": {
        "description": "Additional time added to all transfers (also to transfers between individual to\npublic transport).\n",
        "type": "string",
        "format": "duration",
        "example": "0S"
      },
      "minimalTransferTime": {
        "type": "string",
        "format": "duration"
      }
    }
  },
  "ModeFilter": {
    "type": "object",
    "additionalProperties": false,
    "description": "List of public transport modes to include or exclude. Provided by OJP.\n",
    "properties": {
      "exclude": {
        "description": "Whether modes in list are to include or exclude from search.\n",
        "type": "boolean",
        "default": true
      },
      "transportModes": {
        "description": "List of Transport modes to include/exclude.\n",
        "type": "array",
        "items": {
          "$ref": "#/definitions/Mode"
        }
      }
    }
  },
  "CarrierFilter": {
    "type": "object",
    "additionalProperties": false,
    "description": "Filter for in/exclusion of carriers. Provided by OJP.\n",
    "properties": {
      "exclude": {
        "description": "Whether carrier in list are to include or exclude from search.\n",
        "type": "boolean",
        "default": true
      },
      "carriers": {
        "description": "Reference to carrier\n",
        "type": "array",
        "items": {
          "$ref": "#/definitions/CompanyRef"
        }
      }
    }
  },
  "ServiceBrandFilter": {
    "type": "object",
    "additionalProperties": false,
    "description": "Filter for in/exclusion of service brands. Provided by OJP.\n",
    "properties": {
      "exclude": {
        "description": "Whether service brand in list are to include or exclude from search.\n",
        "type": "boolean",
        "default": true
      },
      "serviceBrands": {
        "description": "Reference to service brand codes\n",
        "type": "array",
        "items": {
          "$ref": "#/definitions/ServiceBrandCode"
        }
      }
    }
  },
  "VehicleFilter": {
    "type": "object",
    "additionalProperties": false,
    "description": "Filter for in/exclusion of vehicle based on vehicle number or line number.\n",
    "properties": {
      "exclude": {
        "description": "Whether vehicle in list is to include or exclude from search.\n",
        "type": "boolean",
        "default": true
      },
      "vehicleNumbers": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/VehicleNumber"
        }
      },
      "lineNumbers": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/LineNumber"
        }
      }
    }
  },
  "PointOfInterestFilter": {
    "type": "object",
    "additionalProperties": false,
    "description": "Filter POIs by category. Provided by OJP.\n",
    "properties": {
      "exclude": {
        "description": "Whether carriers are to be included or excluded from the filtering.\n",
        "type": "boolean",
        "default": true
      },
      "pointOfInterestCategory": {
        "description": "These POI categories can be used to filter POIs. If more than one is given the filtering\nis by logical 'OR' (when Exclude=FALSE) and logical 'AND' (when Exclude=TRUE).\n",
        "type": "array",
        "items": {
          "$ref": "#/definitions/PointOfInterestCategory"
        }
      }
    }
  },
  "TripDataFilter": {
    "type": "object",
    "additionalProperties": false,
    "description": "Data to be included/excluded from search, f.e. modes, carriers. Provided by OJP.\n",
    "properties": {
      "ptModeFilter": {
        "$ref": "#/definitions/ModeFilter"
      },
      "carrierFilter": {
        "$ref": "#/definitions/CarrierFilter"
      },
      "serviceBrandFilter": {
        "$ref": "#/definitions/ServiceBrandFilter"
      },
      "vehicleFilter": {
        "$ref": "#/definitions/VehicleFilter"
      }
    }
  },
  "TripParameters": {
    "type": "object",
    "additionalProperties": false,
    "description": "Options to control the search behavior and response contents.\nnumber of results > either number of results or number of results before and after are used.\nProvided by OJP.\n",
    "properties": {
      "dataFilter": {
        "$ref": "#/definitions/TripDataFilter"
      },
      "policyFilter": {
        "$ref": "#/definitions/BaseTripPolicyFilter"
      },
      "mobilityFilter": {
        "$ref": "#/definitions/TripMobilityFilter"
      },
      "numberOfResults": {
        "type": "integer",
        "format": "int32",
        "default": 5
      },
      "numberOfResultsBefore": {
        "type": "integer",
        "format": "int32",
        "default": 0
      },
      "numberOfResultsAfter": {
        "type": "integer",
        "format": "int32",
        "default": 5
      },
      "ignoreRealtimeData": {
        "type": "boolean",
        "default": false
      },
      "transferLimit": {
        "description": "The maximum number of interchanges the user will accept per trip.\n",
        "type": "integer",
        "format": "int32",
        "minimum": 0
      },
      "itModesToCover": {
        "$ref": "#/definitions/IndividualMode"
      }
    }
  },
  "TripCollectionResponse": {
    "type": "object",
    "additionalProperties": false,
    "description": "Trip response structure. Provided by OJP.\n",
    "required": [
      "id"
    ],
    "properties": {
      "warning": {
        "$ref": "#/definitions/WarningCollection"
      },
      "id": {
        "type": "string",
        "nullable": false
      },
      "trips": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/Trip"
        }
      },
      "tripSummaries": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/TripSummary"
        }
      },
      "_links": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/Link"
        }
      }
    }
  },
  "Trip": {
    "type": "object",
    "additionalProperties": false,
    "description": "A complete trip from origin to destination. Provided by OJP.\n",
    "required": [
      "id",
      "duration",
      "direction",
      "startTime",
      "endTime",
      "transfers",
      "legs"
    ],
    "properties": {
      "id": {
        "description": "Id of this trip for referencing purposes. Unique within a trip.\n",
        "type": "string",
        "nullable": false
      },
      "externalRef": {
        "description": "External reference of this trip for referencing purposes.\n",
        "type": "string"
      },
      "summary": {
        "description": "A human-readable description of the trip.\n",
        "type": "string"
      },
      "duration": {
        "description": "Overall duration of the trip\n",
        "type": "string",
        "format": "duration",
        "nullable": false,
        "example": "PT20M"
      },
      "direction": {
        "$ref": "#/definitions/TripDirectionType"
      },
      "origin": {
        "$ref": "#/definitions/PlaceRef"
      },
      "destination": {
        "$ref": "#/definitions/PlaceRef"
      },
      "startTime": {
        "description": "Departure time at origin.\n",
        "type": "string",
        "format": "date-time",
        "nullable": false
      },
      "endTime": {
        "description": "Arrival time at destination\n",
        "type": "string",
        "format": "date-time",
        "nullable": false
      },
      "transfers": {
        "description": "Number of interchanges\n",
        "type": "integer",
        "format": "int32",
        "minimum": 0,
        "nullable": false
      },
      "distance": {
        "description": "Distance in meters over the the complete trip, i.e., including transfer legs.\n",
        "type": "integer",
        "format": "int32",
        "minimum": 0
      },
      "legs": {
        "description": "Legs ot the trip\n",
        "type": "array",
        "items": {
          "$ref": "#/definitions/TripLeg"
        },
        "minItems": 1
      },
      "operatingDays": {
        "$ref": "#/definitions/OperatingDays"
      },
      "operatingDaysDescription": {
        "description": "Textual description of the operation days, e.g. 'Monday to Friday' or 'Not on holidays'.\n",
        "type": "string"
      },
      "situationFullRefs": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/SituationFullRef"
        }
      },
      "tripStatus": {
        "$ref": "#/definitions/TripStatus"
      },
      "_links": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/Link"
        }
      }
    }
  },
  "TripSummary": {
    "type": "object",
    "additionalProperties": false,
    "description": "Trip summary. Provided by OJP.\n",
    "required": [
      "id"
    ],
    "properties": {
      "id": {
        "description": "Id of this trip for referencing purposes. Unique within trip response.\n",
        "type": "string",
        "nullable": false
      },
      "summary": {
        "type": "string"
      },
      "externalRef": {
        "type": "string"
      },
      "startTime": {
        "description": "Departure time at origin.\n",
        "type": "string",
        "format": "date-time"
      },
      "endTime": {
        "description": "Arrival time at destination.\n",
        "type": "string",
        "format": "date-time"
      },
      "duration": {
        "description": "Overall duration of the trip.\n",
        "type": "string",
        "format": "duration"
      },
      "origin": {
        "$ref": "#/definitions/PlaceRef"
      },
      "destination": {
        "$ref": "#/definitions/PlaceRef"
      },
      "transfers": {
        "type": "integer",
        "format": "int32"
      },
      "distance": {
        "type": "integer",
        "format": "int32"
      }
    }
  },
  "TripStatus": {
    "type": "object",
    "additionalProperties": false,
    "properties": {
      "unplanned": {
        "description": "Whether this trip is an additional one that has not been planned.\n",
        "type": "boolean",
        "default": false
      },
      "cancelled": {
        "description": "Whether this trip is cancelled and will not be run.\n",
        "type": "boolean",
        "default": false
      },
      "deviation": {
        "description": "Whether this trip deviates from the planned service pattern.\n",
        "type": "boolean",
        "default": false
      },
      "delayed": {
        "description": "Whether this trip is delayed.\n",
        "type": "boolean",
        "default": false
      },
      "infeasible": {
        "description": "Whether this trip cannot be used, due to operational delays and impossible transfers.\n",
        "type": "boolean",
        "default": false
      }
    }
  },
  "TripSpecification": {
    "type": "object",
    "additionalProperties": false,
    "description": "A minimal trip specification from origin to destination.\n",
    "required": [
      "legs"
    ],
    "properties": {
      "externalRef": {
        "description": "External reference of this trip for referencing purposes. Unique within a trip specification.\n",
        "type": "string"
      },
      "legs": {
        "type": "array",
        "items": {
          "$ref": "#/definitions/TripLegSpecification"
        },
        "minItems": 1
      },
      "isPartOfInternationalTrip": {
        "type": "boolean",
        "default": false
      }
    }
  },
  "OperatingDays": {
    "type": "object",
    "additionalProperties": false,
    "description": "Day of public transport operation of which the characteristics are defined in a specific\nservice calendar and which may last more than 24 hours. Provided by OJP.\n",
    "required": [
      "from",
      "until",
      "pattern"
    ],
    "properties": {
      "from": {
        "description": "Start date of period\n",
        "type": "string",
        "format": "date-time",
        "nullable": false
      },
      "until": {
        "description": "End date of period\n",
        "type": "string",
        "format": "date-time",
        "nullable": false
      },
      "pattern": {
        "description": "Bit pattern for operating days between start date and end date.\nThe length of the pattern is equal to the number of days from\nstart date to end date. A bit value of '1' indicates that an event\nactually happens on the day that is represented by the bit position.\n",
        "type": "string",
        "nullable": false
      }
    }
  },
  "TripLeg": {
    "type": "object",
    "additionalProperties": false,
    "description": "A single stage of a trip that is made without change of MODE or service (ie:\nbetween each interchange). Provided by OJP.\n",
    "required": [
      "id"
    ],
    "properties": {
      "id": {
        "description": "Id of this trip leg. Unique within trip result. A seqId is a leg id.\n",
        "type": "string",
        "nullable": false
      },
      "externalRef": {
        "description": "External reference of this tripLeg for referencing purposes.\n",
        "type": "string"
      },
      "timedLeg": {
        "$ref": "#/definitions/TimedLeg"
      },
      "transferLeg": {
        "$ref": "#/definitions/TransferLeg"
      },
      "continuousLeg": {
        "$ref": "#/definitions/ContinuousLeg"
      }
    }
  },
  "TripLegSpecification": {
    "type": "object",
    "additionalProperties": false,
    "description": "A minimal trip leg specification.\n",
    "properties": {
      "externalRef": {
        "description": "External reference of this trip leg. Unique within trip result.\n",
        "type": "string"
      },
      "timedLeg": {
        "$ref": "#/definitions/TimedLegSpecification"
      },
      "transferLeg": {
        "$ref": "#/definitions/TransferLeg"
      }
    }
  },
  "TimedLeg": {
    "type": "object",
    "additionalProperties": false,
