      "validFrom": {
        "type": "string",
        "format": "date-time"
      },
      "nutsCodes": {
        "description": "Nomenclature des units territoriales statistiques COMMISSION REGULATION (EU) No 31/2011\n",
        "type": "array",
        "items": {
          "type": "string"
        }
      },
      "zones": {
        "type": "array",
        "items": {
